/**
 * Text data was encountered that can't be converted from/to the relevant
 * text encoding. E.g. trying to write Chinese characters to a file stream
 * opened with the `Latin1` encoding.
 */
export class NoTranslation extends $CustomType {
  constructor(from, to) {
    super();
    this.from = from;
    this.to = to;
  }
}
export const FileStreamError$NoTranslation = (from, to) =>
  new NoTranslation(from, to);
export const FileStreamError$isNoTranslation = (value) =>
  value instanceof NoTranslation;
export const FileStreamError$NoTranslation$from = (value) => value.from;
export const FileStreamError$NoTranslation$0 = (value) => value.from;
export const FileStreamError$NoTranslation$to = (value) => value.to;
export const FileStreamError$NoTranslation$1 = (value) => value.to;

